from collections import defaultdict
from time import time


class Solution:

    def characterReplacement(self, s: str, k: int) -> int:
        start, end = 0, 0
        maxLen = 0
        freq = defaultdict(int)
        freq[s[start]] += 1
        while end < len(s):
            len_sub = end - start + 1
    
            if len(freq) == 1:
                end += 1
                maxLen = max(maxLen, len_sub)
                try:
                    freq[s[end]] += 1
                except IndexError:
                    break
                continue
            
            chars_left = len_sub - max(freq.values()) 
            if k >= chars_left:
                maxLen = max(maxLen, len_sub)
                end += 1
                try:
                    freq[s[end]] += 1
                except IndexError:
                    break
            else:
                freq[s[start]] -= 1
                start += 1

        return maxLen


